mapper.select:
		select * from (select rownum rnum,pr_num,p_code,id,pr_title,pr_content,pr_file,pr_score,pr_hit,pr_wdate
		from (select * from p_review ec where p_code=#{param1} order by pr_wdate desc))
        product where rnum>=#{param2} and rnum<=#{param3}

		select * from (select rownum rnum,cr_num,p_code,id,cr_title,cr_content,cr_score,cr_hit,cr_wdate
		from (select * from c_review ec where p_code=#{param1} order by cr_wdate desc))
        product where rnum>=#{param2} and rnum<=#{param3}
